import {Header} from "./Header";
import {Content} from "./Content";
import {Total} from "./Total";
import { Button } from "./Button";
import { useState } from "react";

const App = () => {
  // const-definitions
const course = [
  { name: "Fundamentals of React", exercises: 10 },
  { name: "Using props to pass data", exercises: 7 },
  { name: "State of a component", exercises: 14 },
];

const [counter, setCounter] = useState({left: 0, right: 0})
const [all, setAll] = useState([])
const [total, setTotal] = useState()

    const addLeft = () => {
        const updateLeft = counter.left + 1
        setCounter({...counter, left: updateLeft})
        setAll(all.concat('L'))
        setTotal(updateLeft + counter.right)
    }
    const addRight = () => {
      const updateRight = counter.right + 1
      setCounter({...counter, right: updateRight})
      setAll(all.concat('R'))
      setTotal(counter.left + updateRight)
    }

const limit = 10

let newTotal = 10

    // if (limit > 1) {
    //     newTotal = 100
    // }

setTotal(newTotal)

  return (
    <div>
      <Header />
      <Content course={course} />
      <Total exercises={course}/>
      <Button handleClick={addLeft} content="Left"/>
      <Button handleClick={addRight} content="Right"/>
      <div>
        <p>left: {counter.left}</p>
        <p>right: {counter.right}</p>
        <p>all: {all.join('-')}</p>
        <p>total: {total}</p>
      </div>
    </div>
  )
}

export default App
